import { Router } from "express";
import { methods as LanguageController } from "./../Controllers/language.controller";

// Mapeo de metodos 

const router = Router();

router.get("/", LanguageController.getLanguages);
router.get("/:Id_Anime", LanguageController.getLanguage);
router.post("/", LanguageController.addLanguages);   
router.delete("/:Id_Anime", LanguageController.deleteLanguage); 
router.put("/:Id_Anime", LanguageController.updateLanguage); 
export default router;

