import { getConnection } from "./../database/database";

//Consultar Todos los datos

const getLanguages = async(req, res) => {

    try {
        const connection = await getConnection();
        const result = await connection.query("SELECT Id_Anime, Nombre_Anime, Temporadas, Genero FROM anime");
        res.json(result);
    } catch (error) {
        res.status(500);
        res.status(error.message);
    }

};

// Consultar un Dato

const getLanguage = async(req, res) => {

    try {
        const{ Id_Anime } = req.params;
        const connection = await getConnection();
        const result = await connection.query("SELECT Id_Anime, Nombre_Anime, Temporadas, Genero FROM anime WHERE Id_Anime = ?", Id_Anime);
        res.json(result);
    } catch (error) {
        res.status(500);
        res.status(error.message);
    }

};

// Agregar un Dato

const addLanguages = async(req, res) => {

    try {
        const {Nombre_Anime,Temporadas,Genero} = req.body;

        if(Nombre_Anime ==undefined || Temporadas == undefined || Genero == undefined)
        {
            res.status(400).json({message: "Bad Request. Please fill all field." });
        }
        const Anime={Nombre_Anime,Temporadas,Genero}
        const connection = await getConnection();
        const result = await connection.query("INSERT INTO anime SET ?", Anime)
        res.json({message: "Dato Insertado"});
    } catch (error) {
        res.status(500);
        res.status(error.message);
    }

};

//Eliminar un Dato

const deleteLanguage = async(req, res) => {

    try {
        const{ Id_Anime } = req.params;
        const connection = await getConnection();
        const result = await connection.query("DELETE FROM anime WHERE Id_Anime = ?", Id_Anime);
        res.json(result);
    } catch (error) {
        res.status(500);
        res.status(error.message);
    }

};

// Actualizar un Dato

const updateLanguage = async(req, res) => {

    try {
        const{ Id_Anime } = req.params;
        const {Nombre_Anime,Temporadas,Genero} = req.body;

        if( Id_Anime== undefined ||Nombre_Anime ==undefined || Temporadas == undefined || Genero == undefined)
        {
            res.status(400).json({message: "Bad Request. Please fill all field." });
        }
        const Anime={ Id_Anime,Nombre_Anime,Temporadas,Genero}
        const connection = await getConnection();
        const result = await connection.query("UPDATE anime SET ? WHERE Id_Anime = ?", [Anime, Id_Anime]);
        res.json(result);
    } catch (error) {
        res.status(500);
        res.status(error.message);
    }

};

// Exportacion  de Methos de API - Rest 

export const methods = {
    getLanguages,
    addLanguages,
    getLanguage,
    deleteLanguage,
    updateLanguage
};

